import styled from 'styled-components';

export const Container = styled.div`
  border: solid;
  display: flex;
  flex-direction: column;
`;

export const Title = styled.h1`
  font-size: 1.5em;
  text-align: left;
  ::after {
    content: ' ';
    display: block;
    width: 100px;
    height: 100px;
    background: green;
  }
`

export const Description = styled.p`
  font-size: 1em;
  text-align: left;
`
