import styled from 'styled-components';
import media from 'styled-media-query';
import theme from '../../styles/theme';
import { CardWithImageTypes } from './types';

export const Container = styled.div`
  display: flex;
`;

export const ImageBox = styled.div<CardWithImageTypes>`
  width: 341px;

  min-width: 163px;
  min-height: 163px;
  display: flex;
  justify-content: flex-end;
  align-items: flex-end;
  background: url( ${prop=> prop.image || 'data:image/png;base64,' });
  background-size: 92% 92%;
  background-repeat: no-repeat;
`

export const Border = styled.div<CardWithImageTypes>`
  width: 90%;
  height: 90%;
  border: green solid 1px;
  ::after{
    content: ${prop => prop.subtitle || 'a'};
    display: block;
    height: 105%;
    display: flex;
    align-items: flex-end;
  }
`

export const TitleAndDescription = styled.div`
  background: linear-gradient(180deg, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0.02) 43.75%);
  backdrop-filter: blur(10px);
  width: 140.86px;
  height: 76.35px;
  position: absolute;
  margin-left: 16px;
  margin-top: 85px;
  padding: 1%;
  overflow: hidden;

  h3 {
    font-size: 0.9rem;
    font-weight: bold;
    color: white;
  }
  span {
    font-size: 0.8rem;
    letter-spacing: 5px;
  }
  p {
    display: none;
  }

  ${media.greaterThan('medium')`
    position: relative;
    margin-left: 20px;
    margin-top: 20px;
    width: 100%;
    overflow: visible;
    background: none;
    h3 {
      color: ${theme.colors.text};
    }
    p {
      display: flex;
      font-style: normal;
      font-weight: normal;
      font-size: 14px;
      line-height: 160%;
      max-height: 130px;
      text-align: left;
      word-spacing: normal;
      overflow: hidden;
      text-overflow: clip;
    }

    span {
      position: absolute;
    }
    `
  }
`
