import styled from 'styled-components';
import media from 'styled-media-query';
import { HeaderTypes } from './types';

export const Container = styled.div<HeaderTypes>`
  width:100%;
  height: 100%;
  background: url( ${prop=> prop.image || 'data:image/png;base64,' });
  background-size: 100% 100%;
  background-repeat: no-repeat;
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  padding: 5%;
  color: white;
`;

export const Title = styled.h2`
    font-style: normal;
    font-weight: bold;
    font-size: 40px;
    line-height: 49px;
    display: flex;
    align-items: center;
    text-transform: uppercase;
    margin-bottom: 10px;
`

export const Description = styled.p`
    font-style: normal;
    font-weight: normal;
    font-size: 1rem;
    line-height: 130%;
    width: 100%;

    ${media.greaterThan('medium')`
      width: 60%;
    `}
`
